import { useState } from "react";
// import Button from 'react-bootstrap/Button';

function Count() {
    
    const [count, setCount] = useState(0)
    const [input, setInput] = useState("")
    function incrementValue (){
        setCount((prevCount)  => count + 1)
        setCount((prevCount)  => + 1)
    }
    function decrementValue () {
        setCount((prevCount)  => - 1)
    }
    function handleOnChange(e){
        setInput((prevInput) => e.target.value);
    }

  return(
    <>
    <div style={{
        backgroundColor: "black",
        display: "flex",
        justifyContent: "center",
        color: "white",
    }}>
      
      <br></br>
      <button style ={{
        height:"50px",
        marginTop: "15px",
      }}type="button" class="btn btn-warning" onClick = {decrementValue}>bawasan mo</button>
      <span style ={{
        margin: "10px",
        padding: "10px",
        fontSize: "25px",
      }}>{count}</span>
      <button style ={{
        height:"50px",
        marginTop: "15px",
      }}type="button" class="btn btn-success" onClick = {incrementValue}>dag-dagan mo</button>
    </div>

    <input onChange={handleOnChange} value={input}></input>
    </>
  );
}
export default Count;
